settings.custom_liquid:
{% comment %} Add this code to your Shopify liquid file where you want the video and product details to appear {% endcomment %}

<div class="video-product-container">
  <div class="product-details">
    <h2>{{ product.title }}</h2>
    <p class="product-description">{{ product.description }}</p>
  </div>

  <div class="video-container">
    <!-- Top Text -->
    <div class="video-top-text">Gold Tone Guide</div>

    <!-- Video Element -->
    <video autoplay loop muted playsinline aria-label="Product showcase video">
      <source src="https://cdn.shopify.com/videos/c/o/v/7fa6b040338a480e83b63a586fa16edd.mp4" type="video/webm">
      Your browser does not support the video tag.
    </video>

    <!-- Bottom Description -->
    <div class="video-bottom-description">
      Explore gold's unique tone, purity, and discover what makes it special.
    </div>
  </div>
</div>

<style>
  .video-product-container {
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 100%;
    padding: 10px;
  }

  .video-container {
    position: relative;
    width: 90%;
    max-width: 800px;
    margin: 20px auto;
    text-align: center;
  }

  .video-container video {
    width: 100%;
    height: auto;
  }

  /* Top Text Style */
  .video-top-text {
    font-family: "oregon-ldo-medium";
    font-size: 40px;
    text-align: center;
    margin-bottom: 10px;
    color: #000; /* Black text */
  }

  /* Bottom Description Style */
  .video-bottom-description {
    font-family: "oregon-ldo-medium";
    font-size: 20px;
    font-weight: normal;
    text-align: center;
    margin-top: 10px;
    color: #000; /* Black text */
  }

  .product-details {
    text-align: center;
    width: 100%;
  }

  .product-details h2 {
    font-size: 20px;
    margin-top: 20px;
    color: black;
  }

  .product-details .product-description {
    display: none;
    font-size: 16px;
  }

  @media only screen and (min-width: 768px) {
    .video-product-container {
      flex-direction: row;
      gap: 20px;
    }

    .video-container, .product-details {
      width: 50%;
    }

    .product-details {
      text-align: left;
    }

    .product-details h2 {
      font-size: 28px;
    }

    .product-details .product-description {
      display: block;
      font-size: 18px;
    }
  }
</style>
